import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { AdminTaskService } from 'src/app/admin-task.service';

@Component({
  selector: 'app-new-list',
  templateUrl: './new-list.component.html',
  styleUrls: ['./new-list.component.scss']
})
export class AdminNewListComponent implements OnInit {
  constructor(private taskService: AdminTaskService, private router: Router, private route: ActivatedRoute) {}

  userId: string;

  ngOnInit() {
    this.route.params.subscribe(
      (params: Params) => {
        this.userId = params.userId;
      }
    )
  }

  createList(title: string){
    this.taskService.createList(this.userId, title).subscribe((response: any) => {
      console.log(response);
      //now we navigate to /lists/response._id
      this.router.navigate(['/admin', this.userId, '/lists', response._id])
    });
  }
}
