import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { AdminTaskService } from 'src/app/admin-task.service';

@Component({
  selector: 'app-edit-list',
  templateUrl: './admin-edit-list.component.html',
  styleUrls: ['./admin-edit-list.component.scss']
})
export class AdminEditListComponent implements OnInit {

  constructor(private route: ActivatedRoute, private adminTaskService: AdminTaskService, private router: Router) { }

  listId: string;
  userId: string;


  ngOnInit() {
    this.route.params.subscribe(
      (params: Params) => {
        this.userId = params.userId;
        this.listId = params.listId;
        console.log(params.listId);
      }
    )
  }

  updateList(title: string) {
    this.adminTaskService.updateList(this.userId, this.listId, title).subscribe(() => {
      this.router.navigate(['admin', this.userId, '/lists', this.listId]);
    })
  }

}
<div class="centered-content">
    <div class="modal-box">
      <h1 class="title">
        Edit list
      </h1>

      <input #listTitleInput class="input has-background-light is-medium" type="text" placeholder="Enter list name...">
      <br><br>
      <div class="buttons is-right">
        <button class="button is-medium" routerLink="/">Cancel</button>
        <button class="button is-primary has-text-white is-medium" (click)="updateList(listTitleInput.value)">Save</button>
      </div>


    </div>
  </div>
