import { Component } from '@angular/core';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent {
  constructor(){}

  ngOnInit(){}

  changePw(oldPw: string, newPw: string, confirmNewPw: string){
    // password fits criteria
    Swal.fire({
      title: 'Error',
      html: 'New password must be at least 8 characters long.',
      icon: 'error',
      backdrop: false
    })

    // check if new pw and confirm new pw is same
    if (newPw !== confirmNewPw){
      Swal.fire({
        title: 'Error',
        text: "Passwords don't match",
        icon: 'error',
        backdrop: false
    })

    // check if new pw is not the same as old pw
    Swal.fire({
      title: 'Error',
      text: 'New password must not be the same as old password',
      icon: 'error',
      backdrop: false
    })

    // check if old pw is correct

    // perform pw change

    }
  }
}
